import React, {useState, useEffect, useContext} from 'react'
import {Link, useParams, useHistory} from 'react-router-dom'
import movieService from "../services/movie-service"
import {AuthContext} from "../App"

const SearchScreen = () => {
    const history = useHistory()
    const {breed} = useParams();
    const [searchTitle, setSearchTitle] = useState(breed)
    const [results, setResults] = useState([])
    const accessToken = useContext(AuthContext);

    useEffect(() => {
        setSearchTitle(breed)
        findMoviesByTitle("dog", accessToken)
        console.log(accessToken)

    }, [])
    const findMoviesByTitle = (breed) => {
        history.push(`/search/${breed}`)
        movieService.findMovieByTitle(breed)
            .then((results) => {
                setResults(results)
                console.log(results)

            })
    }
    return (
        <div>
            <h2>Search Screen{results.length}</h2>
            <input value={searchTitle}
                   onChange={(event) => {
                       setSearchTitle((event.target.value))
                   }}
                   className="form-control"/>
            <button
                onClick={() => {
                    findMoviesByTitle(searchTitle)
                }}
                className="btn btn-primary">Search
            </button>
            <ul className="list-group">


                    {
                        results.map(pet =>
                            <li className="list-group-item">
                                {/*<li className="list-group-item" key={pet.id}>*/}

                                {/*<Link to={`/details/${pet.id}`}>*/}
                                    {pet.name}
                                 {/*</Link>*/}
                            </li>
                        )
                    }
            </ul>

                {/*<li className="list-group-item">*/}
                {/*    <Link to={`/details/dsdsd`}>*/}
                {/*    Titanic*/}
                {/*    </Link>*/}
                {/*</li>*/}
                {/*<li className="list-group-item">*/}
                {/*    Rock Star*/}
                {/*</li>*/}
                {/*<li className="list-group-item">*/}
                {/*    You are lover*/}
                {/*</li>*/}

        </div>
    )

}
export default SearchScreen


