import React, {useState, useEffect} from 'react'
import {Link, useParams, useHistory} from 'react-router-dom'
import movieService from "../services/movie-service"

const SearchScreen = () => {
    const history = useHistory()
    const {breed} = useParams();
    const [searchTitle, setSearchTitle] = useState(breed)
    const [results, setResults] = useState([])
    useEffect(() => {
        setSearchTitle(breed)
        findMoviesByTitle(breed)
        //     if(breed) {
        //         movieService.findMovieByTitle(breed)
        //             .then(results => setResults(results))
        //     }
        // }, [breed])


    }, [])
    const findMoviesByTitle = (breed) => {
        history.push(breed)
        movieService.findMovieByTitle(breed)
            .then((results) => {
                setResults(results)
                console.log(typeof results)

            })
    }
    return (
        <div>
            <h2>Search Screen</h2>
            <input value={searchTitle}
                   onChange={(event) => {
                       setSearchTitle((event.target.value))
                   }}
                   className="form-control"/>
            <button
                onClick={() => {
                    findMoviesByTitle(searchTitle)
                }}
                className="btn btn-primary">Search
            </button>
            <ul className="list-group">

                    {
                        results.map(pet =>

                            <li className="list-group-item" key={pet}>

                                <Link to={`/details/${pet}`}>
                                    {pet}
                                </Link>
                            </li>
                        )
                    }
            </ul>

                {/*<li className="list-group-item">*/}
                {/*    <Link to={`/details/dsdsd`}>*/}
                {/*    Titanic*/}
                {/*    </Link>*/}
                {/*</li>*/}
                {/*<li className="list-group-item">*/}
                {/*    Rock Star*/}
                {/*</li>*/}
                {/*<li className="list-group-item">*/}
                {/*    You are lover*/}
                {/*</li>*/}

        </div>
    )

}
export default SearchScreen

