import React, {useEffect, useState} from 'react'
import  {useHistory, useParams} from 'react-router-dom'
import movieService from '../services/movie-service'


const DetailsScreen = () => {
    const {id} = useParams();
    const  history = useHistory();
    const [pet, setPet] = useState({})
    useEffect(() => {
        findMovieByImdbID()
    }, [])

    const findMovieByImdbID = () => {
        movieService.findMovieByImdbID(id)
            .then((data) => {
                setPet(data)
            })
        console.log(pet)
    }

    return(
        <div>
            <button onClick={() => {history.goBack()}}>Back</button>
            <h2>{pet.name}</h2>
            <p>
                <img src={pet.url} width={150} style={{float:"right"}}/>
                <br/>
                {/*<h5>Description:</h5>{movie.Plot}*/}
            </p>
            <div>
                {pet.name}
                <ul>
                    {
                        pet.temperament && pet.temperament.split(",").map((temp) => {
                            return(
                                <li>
                                    {temp}

                                </li>
                        )
                        })
                    }
                </ul>
            </div>

            {/*{JSON.stringify(pet)}*/}
        </div>
    )

}

export default DetailsScreen
