import React, {useContext, useEffect, useState} from 'react'
import {Link, useHistory, useParams} from 'react-router-dom'
import movieService from '../services/movie-service'
import {AuthContext} from "../App"


const DetailsScreen = () => {
    const {id} = useParams();
    const  history = useHistory();
    const [pet, setPet] = useState({animal:{}})
    const accessToken = useContext(AuthContext);
    useEffect(() => {
        findMovieByImdbID()
    }, [])

    const findMovieByImdbID = () => {
        movieService.findMovieByImdbID(id, accessToken)
            .then((data) => {
                setPet(data)
            })
        console.log(pet)
    }

    return(
        <div>
            <button onClick={() => {history.goBack()}}>Back</button>
            <h2>{pet.animal.name}</h2>
            {JSON.stringify(pet)}

            <div>
                <ul>
                    {

                        <li className="list-group-item" key={pet.animal.id}>
                            <h2>Dog Breed: {pet.animal.name}</h2>
                            <h3>ID: {pet.animal.id}</h3>
                            <h3>Age: {pet.animal.age}</h3>
                            <h3>Status: {pet.animal.status}</h3>
                            <br/>
                            <img src={pet.animal.url} width={250} style={{float:"left"}}/>


                        </li>
                    }
                </ul>
            </div>
        </div>
    )

}

export default DetailsScreen
